//// CS2024:  Lecture 8, Demo #4//// MyString.h//// This is the MyString string class.//class MyString{public:  // Constructors, now overloaded  MyString();  MyString(string initValue);    // Destructor  ~MyString();  string MakeString();    // Here comes the declarations for the overloaded  // readString() member function.  int readString();  int readString(int);    // Method to return a character from internal storage  char charAt(int);private:  char *storagePtr;  char initialStorage[32];  int allocatedSpace;  int stringLength;  // Internal method used to grow storage for our string so it can  // can store a string of the specified size  bool growStorage(int);  // Internal method used to tell us if the specified index is valid  bool isValidIndex(int index)   {    if ((index >= 0) && (index < stringLength))      return true;    else      return false;  }};