//// CS2024:  Lecture #10, Demo 5//// Virtual Functions// #include <iostream>#include <string>using namespace std;#include "Person.h"// Here's the definition for the Person::setInfo() member functionvoid Person::setInfo(string Name,string Addr,string Phone){  name = Name;  address = Addr;  phone = Phone;}// Person::printInfo is now declared virtually in class definition.// Note that we do NOT put the "virtual" keyword herevoid Person::printInfo(){  cout << "Name:  " << name << endl;    // name, address and  cout << "Addr:  " << address << endl; // phone are defined   cout << "Phone: " << phone << endl;   // in the base class}